// Apply Dark Theme - Minified Version
(function(){if(document.querySelector('link[href*="dark-theme"]'))return;const a=document.createElement('link');a.rel='stylesheet';a.href='/src/styles/dark-theme.css';document.head.appendChild(a);const b={toggle:function(){localStorage.getItem('darkMode')==='enabled'?(localStorage.setItem('darkMode','disabled'),document.documentElement.classList.remove('dark')):(localStorage.setItem('darkMode','enabled'),document.documentElement.classList.add('dark'))},check:function(){return localStorage.getItem('darkMode')==='enabled'}};if(b.check())document.documentElement.classList.add('dark');const c=['body','.sidebar','.header','.main-content','.card','.modal','.dropdown-menu','table','.table','input','select','textarea','.form-control','.btn-outline-secondary','.btn-light','.dropdown','.dropdown-toggle','.alert','.page-item','.page-link'],d={backgrounds:['bg-light','bg-white'],texts:['text-dark','text-body']};let e=0;c.forEach(el=>{document.querySelectorAll(el).forEach(item=>{item.classList.add('dark-theme');e++})});d.backgrounds.forEach(cls=>{document.querySelectorAll(`.${cls}`).forEach(item=>{item.classList.add('dark-theme-bg');e++})});d.texts.forEach(cls=>{document.querySelectorAll(`.${cls}`).forEach(item=>{item.classList.add('dark-theme-text');e++})});document.querySelectorAll('iframe').forEach(f=>{try{const g=f.contentDocument||f.contentWindow.document;g.documentElement.classList.add('dark');g.body.classList.add('dark-theme');g.querySelectorAll('div, span, p, h1, h2, h3, h4, h5, h6').forEach(el=>{el.classList.add('dark-theme')})}catch(h){}});const i=window.location.pathname;if(i.includes('/views/home/')||i==='/'){document.querySelectorAll('.card-body').forEach(card=>{card.classList.add('dark-theme')});document.querySelectorAll('.stats-value, .stats-label').forEach(el=>{el.classList.add('dark-theme-text')})}if(i.includes('/views/transacoes/')){document.querySelectorAll('.transaction-item').forEach(item=>{item.classList.add('dark-theme')});document.querySelectorAll('.table-head').forEach(head=>{head.classList.add('dark-theme')})}console.log(`Dark theme applied to ${e} elements.`);return{themeManager:b}})(); 